import React, { useState } from 'react';
import './AutomotiveNews.css'; 
const AutomotiveNews = () => {

  const [newsItems] = useState([
    {
      id: 1,
      title: 'Electrical vehicle registrations',
      summary: 'EV registrations rise 15% in January, as growth continues to slow in U.S.',
      date: 'March 15, 2024',
      url: 'https://www.autonews.com/sales/ev-growth-continues-slow-us-registration-data-shows'
    },
    {
      id: 2,
      title: 'AutoExpo 2024: What to Expect',
      summary: 'The upcoming AutoExpo 2024 is set to showcase the latest in automotive innovation, featuring everything from electric vehicles to autonomous driving systems.',
      date: 'September 25, 2024',
      url: 'https://example.com/autoexpo-2024-preview'
    },
    {
      id: 3,
      title: 'Next-Gen SUVs: Top Picks for 2024',
      summary: 'A rundown of the top next-generation SUVs set to hit the market in 2024, focusing on performance, technology, and sustainability.',
      date: 'October 5, 2024',
      url: 'https://example.com/next-gen-suvs-2024'
    },
    {
      id: 4,
      title: 'The Future of Urban Mobility',
      summary: 'Exploring the future of urban mobility solutions, including public transportation advancements and new vehicle technologies.',
      date: 'October 12, 2024',
      url: 'https://example.com/future-urban-mobility'
    },
    {
      id: 5,
      title: 'Safety Innovations in Vehicle Design',
      summary: 'A look at the latest safety features and innovations being integrated into vehicle designs to protect passengers and pedestrians alike.',
      date: 'October 18, 2024',
      url: 'https://example.com/safety-innovations-vehicles'
    },
  ]);

  return (
    <div className="container">
      <h1>Automotive News</h1>
      {newsItems.length > 0 ? (
        <div>
          {newsItems.map((item) => (
            <div key={item.id} className="newsItem">
              <h2>{item.title}</h2>
              <p>{item.summary}</p>
              <p className="dateStyle">{item.date}</p>
              <a href={item.url} target="_blank" rel="noopener noreferrer" className="link">Read Full Article</a>
            </div>
          ))}
        </div>
      ) : (
        <p>No news available at the moment.</p>
      )}
    </div>
  );
};

export default AutomotiveNews;
