import React, { useState, useEffect } from 'react';
import './CarReviews.css';

const CarReviews = () => {
  const [reviews, setReviews] = useState([]); // Start with an empty array

  const [newReview, setNewReview] = useState({
    model: '',
    review: '',
    rating: '',
  });

  // Fetch reviews from the server when the component mounts
  useEffect(() => {
    fetch('http://localhost:3001/api/reviews')
      .then(response => response.json())
      .then(data => setReviews(data))
      .catch(error => console.error('Error fetching reviews:', error));
  }, []); // Empty dependency array means this effect runs once on mount

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewReview({ ...newReview, [name]: value });
  };

  const addReview = (e) => {
    e.preventDefault();
    const review = { model: newReview.model, review: newReview.review, rating: newReview.rating };
  
    fetch('http://localhost:3001/api/reviews', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(review),
    })
    .then(response => {
      if (response.ok) {
        return fetch('http://localhost:3001/api/reviews'); // Fetch the updated list
      } else {
        throw new Error('Failed to submit review');
      }
    })
    .then(response => response.json())
    .then(data => {
      setReviews(data); // Update state with the latest data
      setNewReview({ model: '', review: '', rating: '' }); // Reset form
    })
    .catch(error => console.error('Error:', error));
  };
  
  return (
    <div className="container">
      <h1>Car Reviews</h1>
      {reviews.length > 0 ? (
        <ul className="reviewList">
          {reviews.map((review) => (
            <li key={review.id} className="reviewItem">
              <h2>{review.model}</h2>
              <p>{review.review}</p>
              <p>Rating: {review.rating}</p>
            </li>
          ))}
        </ul>
      ) : (
        <p>No reviews available at the moment.</p>
      )}

      <form onSubmit={addReview} className="formStyle">
        <h2>Add Your Review</h2>
        <input
          type="text"
          name="model"
          className="inputStyle"
          value={newReview.model}
          onChange={handleInputChange}
          placeholder="Model"
          required
        />
        <textarea
          name="review"
          className="textareaStyle"
          value={newReview.review}
          onChange={handleInputChange}
          placeholder="Review"
          required
        />
        <input
          type="text"
          name="rating"
          className="inputStyle"
          value={newReview.rating}
          onChange={handleInputChange}
          placeholder="Rating"
          required
        />
        <button type="submit" className="buttonStyle">Submit Review</button>
      </form>
    </div>
  );
};

export default CarReviews;
