import React from 'react';
import { Link } from 'react-router-dom';
import { useTranslation } from 'react-i18next'; // Import useTranslation hook from react-i18next
import 'automotive-portal\src\i18n.js'
const Header = () => {
  const { t, i18n } = useTranslation(); // Use the useTranslation hook to access translation function and i18n instance

  // Function to change language
  const changeLanguage = (language) => {
    i18n.changeLanguage(language);
  };

  return (
    <header style={styles.headerStyle}>
      <h1 style={styles.titleStyle}>{t('title')}</h1> {/* Use t function for dynamic translation */}
      <nav>
        <ul style={styles.navListStyle}>
          <li style={styles.navItemStyle}>
            <Link to="/" style={styles.navLinkStyle}>{t('home')}</Link>
          </li>
          <li style={styles.navItemStyle}>
            <Link to="/car-reviews" style={styles.navLinkStyle}>{t('carReviews')}</Link>
          </li>
          <li style={styles.navItemStyle}>
            <Link to="/automotive-news" style={styles.navLinkStyle}>{t('automotiveNews')}</Link>
          </li>
          <li style={styles.navItemStyle}>
            <Link to="/community-forum" style={styles.navLinkStyle}>{t('communityForum')}</Link>
          </li>
          {/* Language selection buttons */}
          <li style={{ ...styles.navItemStyle, marginLeft: 'auto' }}>
            <button onClick={() => changeLanguage('en')} style={styles.navLinkStyle}>EN</button>
          </li>
          <li style={styles.navItemStyle}>
            <button onClick={() => changeLanguage('fr')} style={styles.navLinkStyle}>FR</button>
          </li>
        </ul>
      </nav>
    </header>
  );
};

const styles = {
  headerStyle: {
    backgroundColor: '#333',
    color: '#fff',
    textAlign: 'center',
    padding: '10px',
  },
  titleStyle: {
    margin: '0',
  },
  navListStyle: {
    listStyleType: 'none',
    padding: 0,
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  navItemStyle: {
    margin: '0 10px',
  },
  navLinkStyle: {
    color: '#fff',
    textDecoration: 'none',
    background: 'none',
    border: 'none',
    cursor: 'pointer',
    padding: 0,
  }
};

export default Header;
