import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { LanguageContext } from './LanguageContext'; // Make sure this path is correct

const Header = () => {
  const { toggleLanguage } = useContext(LanguageContext); // Use the useContext hook to access the LanguageContext

  return (
    <header style={styles.headerStyle}>
      <h1 style={styles.titleStyle}>RevHeads</h1>
      <nav>
        <ul style={styles.navListStyle}>
          <li style={styles.navItemStyle}>
            <Link to="/" style={styles.navLinkStyle}>Home</Link>
          </li>
          <li style={styles.navItemStyle}>
            <Link to="/car-reviews" style={styles.navLinkStyle}>Car Reviews</Link>
          </li>
          <li style={styles.navItemStyle}>
            <Link to="/automotive-news" style={styles.navLinkStyle}>Automotive News</Link>
          </li>
          <li style={styles.navItemStyle}>
            <Link to="/community-forum" style={styles.navLinkStyle}>Community Forum</Link>
          </li>
          {/* Language selection buttons */}
          <li style={{ ...styles.navItemStyle, marginLeft: 'auto' }}>
            <button onClick={() => toggleLanguage('en')} style={styles.navLinkStyle}>EN</button>
          </li>
          <li style={styles.navItemStyle}>
            <button onClick={() => toggleLanguage('fr')} style={styles.navLinkStyle}>FR</button>
          </li>
        </ul>
      </nav>
    </header>
  );
};

const styles = {
  headerStyle: {
    backgroundColor: '#333',
    color: '#fff',
    textAlign: 'center',
    padding: '10px',
  },
  titleStyle: {
    margin: '0',
  },
  navListStyle: {
    listStyleType: 'none',
    padding: 0,
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  navItemStyle: {
    margin: '0 10px',
  },
  navLinkStyle: {
    color: '#fff',
    textDecoration: 'none',
    background: 'none',
    border: 'none',
    cursor: 'pointer',
    padding: 0,
  }
};

export default Header;
